import React, { useState } from 'react';
import axios from 'axios';

function SearchCriminal() {
  const [aadharId, setAadharId] = useState('');
  const [criminal, setCriminal] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSearch = async (e) => {
    e.preventDefault();
    setError('');
    setCriminal(null);

    if (!aadharId) {
      setError('Please enter Aadhar ID');
      return;
    }

    if (aadharId.length !== 12) {
      setError('Aadhar ID must be 12 digits');
      return;
    }

    setLoading(true);

    try {
      const response = await axios.get(`http://localhost:5000/api/criminals/search?aadhar_id=${aadharId}`);
      setCriminal(response.data);
    } catch (error) {
      setError(error.response?.data?.error || 'Error searching criminal');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="search-criminal">
      <h1>Search Criminal by Aadhar ID</h1>

      <form onSubmit={handleSearch} className="search-form">
        <div className="form-group">
          <label>Enter Aadhar ID:</label>
          <input
            type="text"
            value={aadharId}
            onChange={(e) => setAadharId(e.target.value)}
            placeholder="12-digit Aadhar ID"
            maxLength="12"
            pattern="[0-9]{12}"
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Searching...' : 'Search'}
        </button>
      </form>

      {error && <div className="error-message">{error}</div>}

      {criminal && (
        <div className="criminal-details">
          <h2>Criminal Record Found</h2>
          <div className="details-grid">
            <div className="detail-item">
              <strong>Aadhar ID:</strong> {criminal.aadhar_id}
            </div>
            <div className="detail-item">
              <strong>Name:</strong> {criminal.name}
            </div>
            <div className="detail-item">
              <strong>Age:</strong> {criminal.age || 'N/A'}
            </div>
            <div className="detail-item">
              <strong>Gender:</strong> {criminal.gender || 'N/A'}
            </div>
            <div className="detail-item">
              <strong>Address:</strong> {criminal.address || 'N/A'}
            </div>
            <div className="detail-item">
              <strong>Crime Type:</strong> {criminal.crime_type}
            </div>
            <div className="detail-item">
              <strong>Crime Details:</strong> {criminal.crime_details || 'N/A'}
            </div>
            <div className="detail-item">
              <strong>Crime Date:</strong> {criminal.crime_date ? new Date(criminal.crime_date).toLocaleDateString() : 'N/A'}
            </div>
            <div className="detail-item">
              <strong>Status:</strong> 
              <span className={`status-${criminal.status.toLowerCase()}`}>
                {criminal.status}
              </span>
            </div>
            {criminal.officer_name && (
              <div className="detail-item">
                <strong>Arresting Officer:</strong> {criminal.officer_name}
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default SearchCriminal;
