import React from 'react';
import { useAuth } from '../contexts/AuthContext';

function Dashboard() {
  const { user } = useAuth();

  const getWelcomeMessage = () => {
    switch (user.role) {
      case 'admin':
        return 'Welcome, System Administrator';
      case 'police':
        return 'Welcome, Police Officer';
      default:
        return 'Welcome to Criminal Records System';
    }
  };

  const getFeatures = () => {
    const baseFeatures = [
      'View your profile',
      'Search criminal records'
    ];

    if (user.role === 'admin') {
      return [
        ...baseFeatures,
        'Manage user registrations',
        'View all criminal records',
        'Add/Edit criminal records',
        'Update criminal status'
      ];
    } else if (user.role === 'police') {
      return [
        ...baseFeatures,
        'View all criminal records',
        'Add new criminal records',
        'Update criminal status'
      ];
    } else {
      return [
        ...baseFeatures,
        'View public criminal records (arrested/convicted only)'
      ];
    }
  };

  return (
    <div className="dashboard">
      <h1>{getWelcomeMessage()}</h1>
      <div className="dashboard-content">
        <div className="user-info">
          <h2>Your Information</h2>
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Aadhar ID:</strong> {user.aadhar_id}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Role:</strong> {user.role}</p>
        </div>
        
        <div className="features">
          <h2>Available Features</h2>
          <ul>
            {getFeatures().map((feature, index) => (
              <li key={index}>{feature}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
