import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

function CriminalManagement() {
  const [criminals, setCriminals] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const { user } = useAuth();

  const [formData, setFormData] = useState({
    aadhar_id: '',
    name: '',
    age: '',
    gender: '',
    address: '',
    crime_type: '',
    crime_details: '',
    crime_date: ''
  });

  useEffect(() => {
    fetchCriminals();
  }, []);

  const fetchCriminals = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/criminals');
      setCriminals(response.data);
    } catch (error) {
      setMessage('Error fetching criminals');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    try {
      await axios.post('http://localhost:5000/api/criminals', formData);
      setMessage('Criminal record added successfully');
      setFormData({
        aadhar_id: '',
        name: '',
        age: '',
        gender: '',
        address: '',
        crime_type: '',
        crime_details: '',
        crime_date: ''
      });
      setShowAddForm(false);
      fetchCriminals();
    } catch (error) {
      setMessage(error.response?.data?.error || 'Error adding criminal');
    }
  };

  const handleStatusUpdate = async (criminalId, status) => {
    try {
      await axios.put(`http://localhost:5000/api/criminals/${criminalId}/status`, { status });
      setMessage('Criminal status updated successfully');
      fetchCriminals();
    } catch (error) {
      setMessage('Error updating criminal status');
    }
  };

  const canAddCriminal = user.role === 'admin' || user.role === 'police';
  const canUpdateStatus = user.role === 'admin' || user.role === 'police';

  if (loading) {
    return <div className="loading">Loading criminal records...</div>;
  }

  return (
    <div className="criminal-management">
      <h1>Criminal Records Management</h1>
      {message && <div className="message">{message}</div>}

      {canAddCriminal && (
        <div className="add-criminal-section">
          <button 
            className="btn-primary"
            onClick={() => setShowAddForm(!showAddForm)}
          >
            {showAddForm ? 'Cancel' : 'Add New Criminal'}
          </button>

          {showAddForm && (
            <form onSubmit={handleSubmit} className="add-criminal-form">
              <h3>Add New Criminal Record</h3>
              <div className="form-row">
                <div className="form-group">
                  <label>Aadhar ID *</label>
                  <input
                    type="text"
                    name="aadhar_id"
                    value={formData.aadhar_id}
                    onChange={handleChange}
                    required
                    maxLength="12"
                    pattern="[0-9]{12}"
                  />
                </div>
                <div className="form-group">
                  <label>Name *</label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Age</label>
                  <input
                    type="number"
                    name="age"
                    value={formData.age}
                    onChange={handleChange}
                  />
                </div>
                <div className="form-group">
                  <label>Gender</label>
                  <select name="gender" value={formData.gender} onChange={handleChange}>
                    <option value="">Select</option>
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                    <option value="Other">Other</option>
                  </select>
                </div>
              </div>

              <div className="form-group">
                <label>Address</label>
                <textarea
                  name="address"
                  value={formData.address}
                  onChange={handleChange}
                  rows="2"
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Crime Type *</label>
                  <input
                    type="text"
                    name="crime_type"
                    value={formData.crime_type}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Crime Date</label>
                  <input
                    type="date"
                    name="crime_date"
                    value={formData.crime_date}
                    onChange={handleChange}
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Crime Details</label>
                <textarea
                  name="crime_details"
                  value={formData.crime_details}
                  onChange={handleChange}
                  rows="3"
                />
              </div>

              <button type="submit" className="btn-primary">Add Criminal Record</button>
            </form>
          )}
        </div>
      )}

      <div className="criminals-list">
        <h2>Criminal Records</h2>
        {criminals.length === 0 ? (
          <p>No criminal records found</p>
        ) : (
          <table className="criminals-table">
            <thead>
              <tr>
                <th>Aadhar ID</th>
                <th>Name</th>
                <th>Age</th>
                <th>Crime Type</th>
                <th>Status</th>
                <th>Crime Date</th>
                {canUpdateStatus && <th>Actions</th>}
              </tr>
            </thead>
            <tbody>
              {criminals.map(criminal => (
                <tr key={criminal.id}>
                  <td>{criminal.aadhar_id}</td>
                  <td>{criminal.name}</td>
                  <td>{criminal.age || 'N/A'}</td>
                  <td>{criminal.crime_type}</td>
                  <td>
                    <span className={`status-${criminal.status.toLowerCase()}`}>
                      {criminal.status}
                    </span>
                  </td>
                  <td>{criminal.crime_date ? new Date(criminal.crime_date).toLocaleDateString() : 'N/A'}</td>
                  {canUpdateStatus && (
                    <td>
                      <select 
                        value={criminal.status}
                        onChange={(e) => handleStatusUpdate(criminal.id, e.target.value)}
                      >
                        <option value="wanted">Wanted</option>
                        <option value="arrested">Arrested</option>
                        <option value="convicted">Convicted</option>
                        <option value="released">Released</option>
                      </select>
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default CriminalManagement;
