import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';
import './CriminalManagement.css'; // Import the new CSS file

function CriminalManagement() {
  const [criminals, setCriminals] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const { user } = useAuth();

  const [formData, setFormData] = useState({
    aadhar_id: '',
    name: '',
    age: '',
    gender: '',
    address: '',
    crime_type: '',
    crime_details: '',
    crime_date: '',
    imageUrl: '' // --- NEW: Added imageUrl to the form state
  });

  useEffect(() => {
    fetchCriminals();
  }, []);

  const fetchCriminals = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/criminals');
      setCriminals(response.data);
    } catch (error) {
      setMessage('Error fetching criminals');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      await axios.post('http://localhost:5000/api/criminals', formData);
      setMessage('Criminal record added successfully');
      setFormData({
        // --- NEW: Reset all fields including imageUrl
        aadhar_id: '', name: '', age: '', gender: '', address: '', crime_type: '',
        crime_details: '', crime_date: '', imageUrl: ''
      });
      setShowAddForm(false);
      fetchCriminals();
    } catch (error) {
      setMessage(error.response?.data?.error || 'Error adding criminal');
    }
  };

  const handleStatusUpdate = async (criminalId, status) => {
    // ... (This function remains the same)
  };

  const canAddCriminal = user.role === 'admin' || user.role === 'police';
  const canUpdateStatus = user.role === 'admin' || user.role === 'police';

  if (loading) {
    return <div className="loading">Loading criminal records...</div>;
  }

  // --- NEW: Function to get initials for placeholder
  const getInitials = (name) => {
    if (!name) return '??';
    const names = name.split(' ');
    if (names.length > 1) {
      return `${names[0][0]}${names[1][0]}`.toUpperCase();
    }
    return name.substring(0, 2).toUpperCase();
  };

  return (
    <div className="criminal-management-page">
      <h1>Criminal Records Management</h1>
      {message && <div className="message">{message}</div>}

      {canAddCriminal && (
        <div className="add-criminal-section">
          <button className="btn-primary" onClick={() => setShowAddForm(!showAddForm)}>
            {showAddForm ? 'Cancel' : 'Add New Criminal'}
          </button>

          {showAddForm && (
            <form onSubmit={handleSubmit} className="add-criminal-form">
              <h3>Add New Criminal Record</h3>
              {/* --- Form fields ... --- */}

              {/* --- NEW: Image URL input field --- */}
              <div className="form-group">
                <label>Image URL</label>
                <input
                  type="text"
                  name="imageUrl"
                  placeholder="https://example.com/image.jpg"
                  value={formData.imageUrl}
                  onChange={handleChange}
                />
              </div>

              <button type="submit" className="btn-primary">Add Criminal Record</button>
            </form>
          )}
        </div>
      )}

      <div className="criminals-list">
        <table className="criminals-table">
          <thead>
            <tr>
              {/* --- NEW: Added Photo column --- */}
              <th>Photo</th>
              <th>Name</th>
              <th>Aadhar ID</th>
              <th>Crime Type</th>
              <th>Status</th>
              <th>Crime Date</th>
              {canUpdateStatus && <th>Actions</th>}
            </tr>
          </thead>
          <tbody>
            {criminals.map(criminal => (
              <tr key={criminal.id}>
                <td>
                  {/* --- NEW: Image rendering logic --- */}
                  {criminal.imageUrl ? (
                    <img src={criminal.imageUrl} alt={criminal.name} className="criminal-photo" />
                  ) : (
                    <div className="photo-placeholder">{getInitials(criminal.name)}</div>
                  )}
                </td>
                <td><span className="criminal-name">{criminal.name}</span></td>
                <td>{criminal.aadhar_id}</td>
                <td>{criminal.crime_type}</td>
                <td>
                  <span className={`status-badge status-${criminal.status.toLowerCase()}`}>
                    {criminal.status}
                  </span>
                </td>
                <td>{criminal.crime_date ? new Date(criminal.crime_date).toLocaleDateString() : 'N/A'}</td>
                {canUpdateStatus && (
                  <td>
                    <select value={criminal.status} onChange={(e) => handleStatusUpdate(criminal.id, e.target.value)}>
                      {/* ... options ... */}
                    </select>
                  </td>
                )}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default CriminalManagement;
